mapper.update:
UPDATE t_product


        name = #{name},


        stock = #{stock}


WHERE id = #{id}

mapper.select:
SELECT id, name, description, price, stock, status, create_time, update_time, is_deleted
FROM t_product

    is_deleted = 0


            AND name LIKE CONCAT('%', #{conditions.name}, '%')


            AND price >= #{conditions.minPrice}


            AND price <= #{conditions.maxPrice}


            AND status = #{conditions.status}






        ORDER BY

            ${sort.orderBy} ${sort.order}



        ORDER BY create_time DESC
